mapper.insert:
insert into rule_reg (ID, RULE_TYPE, RULE_NAME, 
  RULE_URL, CREATE_TIME, CREATOR, 
  UPDATE_TIME, UPDATER, CONTENT
  )
values (#{id,jdbcType=INTEGER}, #{ruleType,jdbcType=VARCHAR}, #{ruleName,jdbcType=VARCHAR}, 
  #{ruleUrl,jdbcType=VARCHAR}, #{createTime,jdbcType=DATE}, #{creator,jdbcType=VARCHAR}, 
  #{updateTime,jdbcType=DATE}, #{updater,jdbcType=VARCHAR}, #{content,jdbcType=LONGVARCHAR}
  )

mapper.select:
	select 
  
  from rule_reg
  where 1=1
  
    and RULE_TYPE = #{record.ruleType,jdbcType=VARCHAR}
  
  
    and RULE_NAME = #{record.ruleName,jdbcType=VARCHAR}
  
  
    and RULE_URL = #{record.ruleUrl,jdbcType=VARCHAR}
  
  
    and CREATE_TIME = #{record.createTime,jdbcType=DATE}
  
  
    and CREATOR = #{record.creator,jdbcType=VARCHAR}
  
  
    and UPDATE_TIME = #{record.updateTime,jdbcType=DATE}
  
  
    and UPDATER = #{record.updater,jdbcType=VARCHAR}
